import logo from "./logo.svg";
import "./App.css";
import React, { Component } from "react";
import { Daily } from "./components/daily-prayer-times/daily-prayer-times.jsx";

//API
// http://api.aladhan.com/v1/calendarByCity?city=Alameda&country=United%20States&method=2&month=Oct&year=2021

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      times: [],
      name: "Obaid",
    }; // end state
  } // end ctro

  componentDidMount() {
    fetch(
      "http://api.aladhan.com/v1/calendarByCity?city=Alameda&country=United%20States&method=2&month=Oct&year=2021"
    )
      .then((response) => response.json())
      .then((users) => this.setState({ times: users }));
  }

  render() {
    return (
      <div>
        <Daily timesObj={this.state.times ? this.state.times : " "} />
        <div>
          Date:
          {this.state.times.data ? this.state.times.data[0].date.readable : " "}
          <div>
            Fajr:
            {this.state.times.data ? this.state.times.data[0].timings.Fajr : ""}
          </div>
          <br />
        </div>
      </div>
    );
  }
} // End class

