  if (dayOfWeekInNumeral == "wed") {
    return 4;
  }
};
const get_todays_date = () => {
  var today = Date();
  today = today.split(" ");
  console.log(today[2]);
  const todaysDate = today[2] - 1;
};

export const Weekly = (props) => {
  get_todays_date();
  get_day_of_week_in_number_form();
